package com.micromall.payment.utils;import java.io.UnsupportedEncodingException;import java.security.MessageDigest;import java.security.NoSuchAlgorithmException;/** * 类名：MD5签名处理类 功能：MD5签名处理核心类 */public class MD5Utils {	/**	 * get the md5 hash of a string	 *	 * @param text	 * @return	 */	public static String md5(String text) {		if (text == null) {			return null;		}		MessageDigest messageDigest;		try {			messageDigest = MessageDigest.getInstance("MD5");			messageDigest.reset();			messageDigest.update(text.getBytes("UTF-8"));		} catch (NoSuchAlgorithmException e) {			return text;		} catch (UnsupportedEncodingException e) {			return text;		}		byte[] byteArray = messageDigest.digest();		StringBuffer md5StrBuff = new StringBuffer();		for (int i = 0; i < byteArray.length; i++) {			if (Integer.toHexString(0xFF & byteArray[i]).length() == 1) {				md5StrBuff.append("0").append(Integer.toHexString(0xFF & byteArray[i]));			} else {				md5StrBuff.append(Integer.toHexString(0xFF & byteArray[i]));			}		}		return md5StrBuff.toString();	}}